using Test

include("reciprocal_cycle.jl")


@testset "#reciprocal_cycles_with_dev" begin
  @test reciprocal_cycles_with_dev(10) == (6, "0.142857")
  @test reciprocal_cycles_with_dev(100) == (96,
                                            "0.010309278350515463917525773195876288659793814432989690721649484536082474226804123711340206185567")

#   # @test reciprocal_cycles_with_dev(800) == (743,
#   #                                          "0.")
end

@testset "#reciprocal_cycles" begin
  @test reciprocal_cycles(800) == 743

  @test reciprocal_cycles(900) == 887

  @test reciprocal_cycles(1000) == 983

  @test reciprocal_cycles(700) == 659
end

@testset "#reciprocal_cycles exception" begin
  @test_throws ArgumentError reciprocal_cycles(-5)
  @test_throws ArgumentError reciprocal_cycles("foobar")
end
